CREATE TABLE SYS_ALARM (ID varchar(32) NOT NULL, SQLID varchar(48), FTLID varchar(48), SERVICE varchar(128), PARAM varchar(256), CHECKRULE varchar(1024), CHECKINTERVAL int NOT NULL, LASTCHECKSEC int, LASTCHECKTM varchar(20), LASTALARMTM varchar(20), LASTALARMMSG varchar(512), PHONE varchar(1028) NOT NULL, STATUS char(1), REMARK varchar(128), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), MSGQ varchar(24), MSGFTLID varchar(24), SYS varchar(12), PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_CONFIG (CODE varchar(128) NOT NULL, VAL varchar(512) NOT NULL, DTYPE varchar(10) NOT NULL, STATUS char(1), MODEL varchar(10) NOT NULL, REMARK varchar(60), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), PRIMARY KEY (CODE)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_DICT (CODE varchar(50) NOT NULL, NAME varchar(50) NOT NULL, DTYPE varchar(50) NOT NULL, DLEVEL varchar(50) NOT NULL, PARENTCODE varchar(50), DORDER int NOT NULL, SYS varchar(12), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), PRIMARY KEY (CODE, DTYPE)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_FTL (ID varchar(24) NOT NULL, FTL varchar(4000) NOT NULL, REMARK varchar(128), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), SYS varchar(12), FTL1 varchar(4000), FTL2 varchar(4000), FTL3 varchar(4000), PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_JS (ID varchar(30) NOT NULL, JS varchar(4000) NOT NULL, REMARK varchar(128), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), SYS varchar(12), JS1 varchar(4000), JS2 varchar(4000), JS3 varchar(4000), PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_MENU (MID varchar(24) NOT NULL, TEXT varchar(32) NOT NULL, PARENTID varchar(24), WIN varchar(32), JS varchar(64), CONFIG varchar(256), MORDER int, SERVICE varchar(4000), SQLID varchar(4000), MMENU char(1), PRIMARY KEY (MID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_RETCODE (RETCD varchar(16) NOT NULL, TEXT varchar(1024) NOT NULL, ACTION varchar(512), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), PRIMARY KEY (RETCD)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_ROLE (ID varchar(12) NOT NULL, NAME varchar(20), MENU varchar(256), REMARK varchar(64), PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_SEQNO (NAME varchar(24) NOT NULL, SEQNO bigint, MAXSEQNO bigint, RECYCLENO bigint, BATCHSIZE int, VER int, REMARK varchar(200), PRIMARY KEY (NAME)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_SQL (MDL varchar(24) NOT NULL, ID varchar(60) NOT NULL, RESULTCLASS varchar(128), TYPE int NOT NULL, DS varchar(24), FIRSTROWONLY char(1), PREPARED char(1), PROC char(1), TEXT varchar(4000) NOT NULL, REMARK varchar(128), USERCD varchar(24), LASTUPDTM varchar(20), ACTIONNM char(1), VERDT varchar(8), VERSTATUS char(1), SYS varchar(12), TEXT1 varchar(4000), TEXT2 varchar(4000), TEXT3 varchar(4000), AUTH varchar(10), PRIMARY KEY (MDL, ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE SYS_USER (CODE varchar(24) NOT NULL, NAME varchar(24), PWD varchar(50), ORGID varchar(12), ROLEID varchar(12), IPADDRESS varchar(20), EMAIL varchar(20), AUTORUNS varchar(100), PWDSALT char(32), PRIMARY KEY (CODE)) ENGINE=InnoDB DEFAULT CHARSET=utf8;


















